import {useState} from 'react'
import quotes from 'quotesy'

function App() {
  const[quote,setQuote] = useState(()=>{
    return quotes.random()
  })
  function getQuotes(){
    setQuote(()=>{
      return quotes.random()
    })
  }
  return (
    <div id='quote-box'>
      <p id='text'>{quote.text}</p>
      <p id='author'>{quote.author}</p>
      <button id='new-qu' onClick={getQuotes}>Get</button>
    </div>

  );
}

export default App;
